import './detalhes.css'
import { Row, Col, Button, Input, FormGroup, Jumbotron } from 'reactstrap';
import { useLocation } from 'react-router';
import N_A from './n-a.jpg'

class Detalhes extends React.Component{

    state={
         Title:[],
         Plot: [],
         imdbRating: [],
         Runtime: [],
         Poster: [],
         imdbID: [],
