        // if(data.Response == 'False' || this.variables.Awards == undefined || this.variables.Awards == '"N/A"')
        //   {this.variables.Awards = '"Indefinido"'}
        // if(data.Response == 'False' || this.variables.Production == undefined || this.variables.Production == '"N/A"')
        //   {this.variables.Production = '"Indefinido"'}
        // if(data.Response == 'False' || this.variables.Year == undefined || this.variables.Year == '"N/A"')
        //   {this.variables.Year = '"Indefinido"'}
        // if(data.Response == 'False' || this.variables.Language == undefined || this.variables.Language == '"N/A"')
        //   {this.variables.Language = '"Indefinido"'}
        // if(data.Response == 'False' || this.variables.Actors == undefined || this.variables.Actors == '"N/A"')
        //   {this.variables.Actors = '"Indefinido"'}
        // if(data.Response == 'False' || this.variables.Poster == undefined || this.variables.Poster == '"N/A"')
        //   {this.variables.Poster = N_A}

        //   if(JSON.parse(imdbRating) <= 0){
        //   for(var i = 0;i<5;i++)
