import {React,Component} from 'react';
import './catalogo.css';
import N_A from './n-a.jpg'
import { Row, Col, Button, Input, FormGroup } from 'reactstrap';


class Catalogo extends Component {

  state = {
    Resultado: []
  }

  get(){
    fetch("http://127.0.0.2:8080/movie")
    .then(res => res.json())
    .then(
        (result) => {
        this.setState({Resultado: result})
        }
    )}

    render(){
        return (
            <div>
                <Row>
                  <Col md='2'>
                      <a className="bgfime" href="/detalhes/asdasd">
                          <Col md='12'>
                              <img className="poster" src={N_A} alt="poster"/>
                          </Col>
                          <div class="poster-text">
                              <h3>asdas</h3>
                              <p>Plot</p>
                              <FormGroup>
                                  <small id="notaFilme">Nota: mdbRating</small>
                                  <small id="tempoFilme">Runtime</small>
                              </FormGroup>
                          </div>
                      </a>
                  </Col>
                </Row>      
            </div>            
        ); 
    }
}


  
export default Catalogo;
  

