          {this.state.Actors = '"Indefinido"'}
        if(data.Response == 'False' || this.state.Poster == undefined || this.state.Poster == '"N/A"')
          {this.state.Poster = N_A}

          if(JSON.parse(imdbRating) <= 0){
          for(var i = 0;i<5;i++)
          Star.push('fa fa-star-o')
        }

