          Star.push('fa fa-star')
          Star.push('fa fa-star')
          for(var i = 0;i<3;i++)
          Star.push('fa fa-star-o')
        }

        if(JSON.parse(imdbRating) > 4 && JSON.parse(imdbRating) < 6){
          Star.push('fa fa-star')
