import React from 'react';
import './catalogo.css';
import N_A from './n-a.jpg'
import { Row, Col, Button, Input, FormGroup } from 'reactstrap';
import * as $ from 'jquery'

function Catalogo() {

    $.ajax({
        method: "get",
        // url: "http://www.omdbapi.com/?apikey=2729c5a&i=tt"+Math.floor(Math.random() * 1500000),
        url: "http://127.0.0.2:8080/movie",
        success: (data)=>{     

        var Title = JSON.stringify(data.Title);
        var Plot = JSON.stringify(data.Plot);
        var imdbRating = JSON.stringify(data.imdbRating)
        var Runtime = JSON.stringify(data.Runtime)
        // var Poster = JSON.stringify(data.Poster)
        // var imdbID = JSON.stringify(data.imdbID)

        if(Poster == undefined || Poster == '"N/A"'){
          Poster = JSON.stringify("./n-a.jpg")
        }

        return (
            <div>
                <Row>
                  <Col md='2'>
                      <a className="bgfime" href="/detalhes/{imdbId}">
                          <Col md='12'>
                              <img className="poster" src={Poster} alt="poster"/>
                          </Col>
                          <div class="poster-text">
                              <h3>{Title}</h3>
                              <p>{Plot}</p>
                              <FormGroup>
                                  <small id="notaFilme">Nota: {imdbRating}</small>
                                  <small id="tempoFilme">{Runtime}</small>
                              </FormGroup>
                          </div>
                      </a>
                  </Col>
                </Row>      
            </div>            
          );

       }
     })    
    
  }
  
  export default Catalogo;
