import React from 'react';
import { Row, Col, Button, Input, FormGroup } from 'reactstrap';
import { useLocation } from 'react-router';

class Detalhes extends React.Component{

    state={
         Title:[],
         Plot: [],
         imdbRating: [],
         Runtime: [],
         Poster: [],
         imdbID: [],
         Diretor: [],
         Writer: [],
         Awards: [],
         Production: [],
         Year: [],
         Language: [],
         Actors: [],
         Star: []    
    }

    url=new URLSearchParams(window.location.href);

    verifyUrl(){
        if(this.url.includes('tt') == false){
            this.url = "http://www.omdbapi.com/?apikey=2729c5a&t="+ window.location.href.split('/')[4]
        }else{
            this.url = "http://www.omdbapi.com/?apikey=2729c5a&i="+ window.location.href.split('/')[4]
        }
    }
    
  componentDidMount() {
    var random = Math.floor(Math.random() * 1500000) + 1000000
    const apiUrl = 'http://127.0.0.2:8080/movie';   
    // const apiUrl = "http://www.omdbapi.com/?apikey=2729c5a&i=tt"+ random
    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => {
          this.setState({Title: data.Title})
          this.setState({Plot: data.Plot})
          if(data.Poster == "N/A" || data.Poster == undefined || data.Response == 'False'){
            // this.setState({Poster: N_A})
          }else{
            this.setState({Poster: data.Poster})
          }
          this.setState({mdbId: '/detalhes/' + data.imdbID})
          this.setState({mdbRating: data.mdbRating})
          this.setState({Runtime: data.Runtime})
      });
  }

  render() {
     return (
        <div>
            <h1>Ol√°</h1>
            <a href="/">{this.url}</a>
        </div>
     )
  }
  
}

  
export default Detalhes;
